CREATE OR REPLACE TRIGGER NEW_APDEV.CREATE_DATA_COLLECT
AFTER UPDATE
ON NEW_APDEV.POLICY REFERENCING NEW AS UPDATED_POLICY OLD AS OLD_POLICY
FOR EACH ROW
WHEN (
UPDATED_POLICY.POLICY_STATUS_CODE IN ('F','E')
AND OLD_POLICY.POLICY_STATUS_CODE NOT IN ('F','E')
AND (CASE WHEN UPDATED_POLICY.STATE_ALPHA_CODE = 'NJ'
       AND UPDATED_POLICY.GROUP_LINE_CODE || UPDATED_POLICY.MOST_RECENT_REASON_ACTION || UPDATED_POLICY.MOST_RECENT_REASON_TYPE || UPDATED_POLICY.CONVERTED = '75*RCY'
       AND UPDATED_POLICY.POLICY_EFFECTIVE_DATE BETWEEN TO_DATE('5/1/2014','MM/DD/YYYY') AND TO_DATE('4/30/2015','MM/DD/YYYY') THEN 0
      WHEN UPDATED_POLICY.STATE_ALPHA_CODE = 'MA'
       AND UPDATED_POLICY.GROUP_LINE_CODE || UPDATED_POLICY.MOST_RECENT_REASON_ACTION || UPDATED_POLICY.MOST_RECENT_REASON_TYPE || UPDATED_POLICY.CONVERTED = '75*RCY'
       AND UPDATED_POLICY.POLICY_EFFECTIVE_DATE BETWEEN TO_DATE('6/1/2013','MM/DD/YYYY') AND TO_DATE('5/31/2014','MM/DD/YYYY') THEN 0
     ELSE 1 END) = 1  -- added to allow MA Compak converted policy renewals through this trigger.
AND UPDATED_POLICY.USER_ENTERED NOT IN ('CONVERT_POLICY_PKG','njtest','matest','AUTONDEMPQA')
--AND UPDATED_POLICY.USER_ENTERED NOT IN ('CONVERT_POLICY_PKG','njtest','matest')
