(SELECT ITEM.POLICY_NUMBER, ITEM.WANG_ITEM_SEQ, IRC.COVERAGE_CODE, IRC.PREMIUM
  FROM  NEW_APDEV.ITEM_AT_RISK ITEM, NEW_APDEV.ITEM_AT_RISK_COVERAGE IRC
 WHERE :UPDATED_POLICY.GROUP_LINE_CODE= '46'
   AND :UPDATED_POLICY.POLICY_NUMBER = ITEM.POLICY_NUMBER
   AND ITEM.ITEM_AT_RISK_ID = IRC.ITEM_AT_RISK_ID
   AND IRC.COVERAGE_CODE NOT IN ('335','994')) AP
WHERE  RC.POLICY_NUMBER = :UPDATED_POLICY.POLICY_NUMBER
      AND AP.COVERAGE_CODE(+) = RC.COV_CODE
      AND RC.COV_CODE NOT IN  ('335','994'))
WHERE :UPDATED_POLICY.GROUP_LINE_CODE= '46';


