/* CREATE TEMPORARY TABLE TO INDICATE ENDORSEMENT ACTION */
INSERT INTO COV_ACTION_TEMP
SELECT :UPDATED_POLICY.POLICY_NUMBER, ITEM.ITEM_AT_RISK_ID, IC.COVERAGE_CODE, 'A' ACTION_CODE, :UPDATED_POLICY.DATE_ENTERED
FROM NEW_APDEV.ITEM_AT_RISK ITEM, NEW_APDEV.ITEM_AT_RISK_COVERAGE IC,
     (select DECODE(COMPAK_CONV, 'Y', '999',  min(wang_item_seq)) MIN_ITEM_SEQ, policy_number
                   from new_apdev.item_at_risk iar
                  where :UPDATED_POLICY.POLICY_NUMBER = policy_number
                    and wang_item_seq <> 0
                 group by policy_number) min_item
WHERE :UPDATED_POLICY.POLICY_NUMBER = ITEM.POLICY_NUMBER
AND ITEM.ITEM_AT_RISK_ID = IC.ITEM_AT_RISK_ID
AND NOT EXISTS (SELECT 1
                  FROM RPTVIEWER.RPT_COVERAGE C
                 WHERE ITEM.POLICY_NUMBER = C.POLICY_NUMBER
                   AND DECODE(ITEM.WANG_ITEM_SEQ, 0, MIN_ITEM_SEQ, ITEM.WANG_ITEM_SEQ) = C.ITEM_SEQ
                   AND C.COV_CODE = IC.COVERAGE_CODE)
UNION
SELECT :UPDATED_POLICY.POLICY_NUMBER, ITEM.ITEM_AT_RISK_ID, IC.COVERAGE_CODE, 'C' ACTION_CODE, :UPDATED_POLICY.DATE_ENTERED
FROM NEW_APDEV.ITEM_AT_RISK ITEM, NEW_APDEV.ITEM_AT_RISK_COVERAGE IC
WHERE :UPDATED_POLICY.POLICY_NUMBER = ITEM.POLICY_NUMBER
AND ITEM.ITEM_AT_RISK_ID = IC.ITEM_AT_RISK_ID
AND EXISTS (SELECT 1
              FROM RPTVIEWER.RPT_COVERAGE C,
              (select DECODE(COMPAK_CONV, 'Y', '999',  min(wang_item_seq)) MIN_ITEM_SEQ, policy_number
                   from new_apdev.item_at_risk iar
                  where :updated_policy.policy_number = policy_number
                    and wang_item_seq <> 0
                 group by policy_number) min_item
             WHERE ITEM.POLICY_NUMBER = C.POLICY_NUMBER
               AND C.POLICY_NUMBER = MIN_ITEM.POLICY_NUMBER
               AND decode(ITEM.WANG_ITEM_SEQ, '0', MIN_ITEM_SEQ, ITEM.WANG_ITEM_SEQ)  = C.ITEM_SEQ
               AND IC.COVERAGE_CODE = C.COV_CODE);

