SELECT
/* 0001-0001 */ '4' TRANSACTION_TYPE,
/* 0002-0003 */ SUBSTR(TO_CHAR(:UPDATED_POLICY.POLICY_EFFECTIVE_DATE,'YYYYMMDD'),1,2) KEY_DATE_CC,
/* 0004-0009 */ SUBSTR(TO_CHAR(:UPDATED_POLICY.POLICY_EFFECTIVE_DATE,'YYYYMMDD'),3,6) KEY_DATE,
/* 0010-0039 */ RPAD(' ',30,' ') AI_CUSTOMER_ID,
/* 0040-0041 */ RPAD(NVL(:UPDATED_POLICY.ISSUE_COMPANY_CODE,' '),2,' ') COMPANY,
/* 0042-0043 */ RPAD(NVL(:UPDATED_POLICY.GROUP_LINE_CODE,' '),2,' ') LINE_OF_BUSINESS,
/* 0044-0049 */ RPAD(SUBSTR(:UPDATED_POLICY.POLICY_NUMBER,1,6),6,' ') KEY_VARIABLE_SEQ,
/* 0050-0054 */ RPAD(SUBSTR(:UPDATED_POLICY.POLICY_NUMBER,7,5),5,' ') KEY_VARIABLE_WIP,
/* 0055-0072 */ RPAD(' ',18,' ') FILLER_0055_0072,
/* 0073-0073 */ RPAD(' ',1,' ') UPLOAD_LOG_KEY,
/* 0074-0081 */ RPAD(NVL(:UPDATED_POLICY.AGENCY_NUMBER,' '),8,' ') AGENCY,
/* 0082-0111 */ RPAD(NVL(NAME.NAME,' '),30,' ') UNAME,
/* 0112-0121 */ RPAD(NVL(NAME.LAST_NAME,' '),10,' ') INSURED_NAME,
/* 0122-0131 */ RPAD(:UPDATED_POLICY.POLICY_NUMBER,10,' ') POLICY_NUMBER,
/* 0132-0139 */ TO_CHAR(:UPDATED_POLICY.DATE_ENTERED,'YYYYMMDD') SUBMISSION_DATE,
/* 0140-0149 */ LPAD(trunc(:UPDATED_POLICY.DEPOSIT_AMOUNT),8,'0') || DECODE( INSTR(:UPDATED_POLICY.DEPOSIT_AMOUNT,'.'),0,'00',RPAD(SUBSTR(:UPDATED_POLICY.DEPOSIT_AMOUNT,INSTR(:UPDATED_POLICY.DEPOSIT_AMOUNT,'.')+1,2),2,'0')) DEPOSIT,
/* 0150-0151 */ RPAD(NVL(:UPDATED_POLICY.GROUP_LINE_CODE,' '),2,' ') GROUP_LINE,
/* 0152-0152 */ RPAD(' ',1,' ') CHECK_RECEIVED_FLAG,
/* 0153-0160 */ TO_CHAR(:UPDATED_POLICY.POLICY_EFFECTIVE_DATE,'YYYYMMDD') CONVERT_DATE,
/* 0161-0168 */ RPAD(' ',8,' ') SDS_DATE,
/* 0169-0169 */ RPAD(' ',1,' ') CHECK_MOD_FLAG,
/* 0170-0172 */ RPAD(' ',3,' ') CHECK_MOD_USER,
/* 0173-0180 */ RPAD(' ',8,' ') CHECK_MOD_DATE,
/* 0181-0193 */ RPAD(' ',13,' ') CHECK_NUMBER,
/* 0194-0194 */ RPAD(' ',1,' ') LATE_SUBMISSION,
/* 0195-0195 */ RPAD(' ',1,' ') AGENT_MOD_FLAG,
/* 0196-0200 */ '00000' WIP_NUMBER,
/* 0201-0240 */ RPAD(' ',40,' ') DOCUMENT_AUDIT,
/* 0241-0241 */ RPAD(' ',1,' ') BAD_POLICY_FLAG,
/* 0242-0251 */ RPAD(' ',10,' ') POLICY_ORIGINAL,
/* 0252-1000 */ RPAD(' ',300,' ') FILLER_0252,
:UPDATED_POLICY.DATE_ENTERED DATE_ENTERED
FROM NEW_APDEV.ITEM_AT_RISK ITEM, NEW_APDEV.ITEM_NAME NI, NEW_APDEV.NAME
WHERE :UPDATED_POLICY.POLICY_NUMBER = ITEM.POLICY_NUMBER
